import React from 'react';
import { Section, Row, Col, TextInput, Icon, Button } from 'react-materialize';

export default function Login() {
  const initialFormData = Object.freeze({
    email: "",
    password: ""
  });
  const [formData, updateFormData] = React.useState(initialFormData);

  const handleChange = (e) => {
    updateFormData({
      ...formData,

      // Trimming any whitespace
      [e.target.name]: e.target.value.trim()
    });
  };
  const onLoginClick = () => {
    console.log('Form Submited');
  };

  return (
    <Section className="container">
      <Row>
        <Col
          s={12}
          m={4}
          xl={4}
          offset="m4 xl4"
          className="z-depth-4 card-panel border-radius-6 login-card bg-opacity-8"
        >
          <div className="login-form">
            <Row>
              <h4 className="header center-align">Login</h4>
            </Row>
            <Row>
              <TextInput
                id="email"
                icon={<Icon>email</Icon>}
                label="Email"
                s={12}
              />
            </Row>
            <Row>
              <TextInput
                id="password"
                type="password"
                icon={<Icon>lock_outline</Icon>}
                label="Password"
                s={12}
              />
            </Row>
            <Row>
              <Col s={12} className="input-field">
                <Button
                  waves="light"
                  className="col s12"
                  onClick={onLoginClick}
                >
                  Login
                </Button>
              </Col>
            </Row>
          </div>
        </Col>
      </Row>
    </Section>
  );
}
