import React from "react";
import { Navbar, NavItem, Icon } from "react-materialize";
import { LANDING_URL, LOGIN_URL, REGISTER_URL } from "./../../routes";
export default function Navigation() {
  const onLogout = (e) => {
    e.preventDefault()
    axios.post('http://localhost:5000/api/users/login', {
      email: formData.email,
      password: formData.password
    })
    .then(function (response) {
      localStorage.setItem("token", response);
      history.push("/dashboard");
    })
    .catch(function (error) {
      console.log(error);
    }); 
  };
  return (
    <Navbar
      centerChildren
      alignLinks="right"
      brand={
        <a className="brand-logo" href={LANDING_URL}>
          Lab Three
        </a>
      }
      id="mobile-nav"
      menuIcon={<Icon>menu</Icon>}
      options={{
        draggable: true,
        edge: "left",
        inDuration: 250,
        onCloseEnd: null,
        onCloseStart: null,
        onOpenEnd: null,
        onOpenStart: null,
        outDuration: 200,
        preventScrolling: true,
      }}
    >
      <Route {...rest} render={props => (
          localStorage.getItem('token') ?
          <div>
            <NavItem href={LOGIN_URL}>Login</NavItem>
            <NavItem href={REGISTER_URL}>Register</NavItem>
          </div>
          : <NavItem onClick={()=> onLogout()}>Log Out</NavItem>
      )} />
    </Navbar>
  );
}
