import React from 'react';
import { Section, Row, Col, TextInput, Icon, Button } from 'react-materialize';
import { useHistory } from "react-router-dom";
import axios from 'axios';

export default function Register() {
const initialFormData = Object.freeze({
  email: "",
  password: "",
  password2: ""
});
const [formData, updateFormData] = React.useState(initialFormData);

const handleChange = (e) => {
  updateFormData({
    ...formData,

    // Trimming any whitespace
    [e.target.name]: e.target.value.trim()
  });
};

  const onRegisterClick = (e) => {
    e.preventDefault()
    axios.post('http://localhost:5000/api/users/register', formData, {
      headers:  {}
    });
    history.push("/login") 
  };

  return (
    <Section className="container">
      <Row>
        <Col
          s={12}
          m={4}
          xl={4}
          offset="m4 xl4"
          className="z-depth-4 card-panel border-radius-6 register-card bg-opacity-8"
        >
          <div className="register-form">
            <Row>
              <h4 className="header center-align">Register</h4>
            </Row>
            <Row>
              <TextInput
                id="email"
                name="email"
                icon={<Icon>email</Icon>}
                onChange={handleChange}
                label="Email"
                s={12}
              />
            </Row>
            <Row>
              <TextInput
                id="password"
                name="password"
                type="password"
                icon={<Icon>lock_outline</Icon>}
                onChange={handleChange}
                label="Password"
                s={12}
              />
            </Row>
            <Row>
              <TextInput
                id="password2"
                name="password2"
                type="password"
                icon={<Icon>lock_outline</Icon>}
                onChange={handleChange}
                label="Password"
                s={12}
              />
            </Row>
            <Row>
              <Col s={12} className="input-field">
                <Button
                  waves="light"
                  className="col s12"
                  onClick={onRegisterClick}
                >
                  Register
                </Button>
              </Col>
            </Row>
          </div>
        </Col>
      </Row>
    </Section>
  );
}
